<?php// no direct access	defined('SITE_KEY') or die('File not found - <a href="/">please click here to return to the home page</a>');		//	are we UPDATing or INSERTing	if ($_POST['export_data_profile_id'] == 'new')	{		$sql_1 = 'INSERT INTO';		$sql_2 = '';	}	else	{		$profile_id = $_POST['export_data_profile_id'];		$sql_1 = 'UPDATE';		$sql_2 = ' WHERE profile_id = "'.$profile_id.'"';	}		//	are We DELETING	if (isset($_POST['submit_delete_profile']))	{		//	DELETE RECORDS		$sql_statement = 'DELETE from sweeps_export_data_profiles WHERE profile_id = "'.$profile_id.'"';		UpdateDB ($sql_statement, $mysql_err_msg);							$sql_statement = 'DELETE from sweeps_export_data_fields WHERE profile_id = "'.$profile_id.'"';		UpdateDB ($sql_statement, $mysql_err_msg);						$sql_statement = 'DELETE from sweeps_export_data_filters WHERE profile_id = "'.$profile_id.'"';		UpdateDB ($sql_statement, $mysql_err_msg);						$sql_statement = 'DELETE from sweeps_export_data_order_by WHERE profile_id = "'.$profile_id.'"';		UpdateDB ($sql_statement, $mysql_err_msg);			header('location: '.$this_page); 		exit();				}		$mysql_err_msg = 'Updating Export Data Profile';	//	update sweeps_export_data_profiles	$sql_statement = $sql_1.' sweeps_export_data_profiles SET'									.'  name = "'.$_POST['export_data_profile_name'].'"'									.', category = "'.$_POST['export_data_category'].'"'									.', file_type = "'.$_POST['export_data_file_type'].'"'									.', file_name = "'.$_POST['export_data_filename'].'"'									.', filename_suffix = "'.$_POST['export_data_file_suffix'].'"'									.', print_headers = "'.$_POST['export_data_print_headers'].'"'																		.	$sql_2									;//echo $sql_statement;													UpdateDB ($sql_statement, $mysql_err_msg);				if ($_POST['export_data_profile_id'] == 'new')	{			$profile_id = mysql_insert_id();	}		//	delete all records in: sweeps_export_data_fields first and than re populate with updated data	$sql_statement = 'DELETE from sweeps_export_data_fields WHERE profile_id = "'.$profile_id.'"';	UpdateDB ($sql_statement, $mysql_err_msg);		for ($num = 1; $num < $_POST['export_data_num_fields'] + 1; $num++)	{		if ($_POST['export_data_selected_field_' . $num] != '')		{			$sql_statement = 'INSERT INTO sweeps_export_data_fields SET' 														.'  profile_id = "'.$profile_id.'"'											.', seq = "'.$num.'"'											.', fields_selected = "'.$_POST['export_data_selected_field_' . $num].'"'											;				UpdateDB ($sql_statement, $mysql_err_msg);			}	}	//	Update / Insert records for sweeps_export_data_filters and sweeps_export_data_order_by		//	delete all records first and than re populate with updated data	$sql_statement = 'DELETE from sweeps_export_data_filters WHERE profile_id = "'.$profile_id.'"';	UpdateDB ($sql_statement, $mysql_err_msg);	$sql_statement = 'DELETE from sweeps_export_data_order_by WHERE profile_id = "'.$profile_id.'"';	UpdateDB ($sql_statement, $mysql_err_msg);			for ($num = 1; $num < 4; $num++)	{		if (isset($_POST['export_data_filter_active_' . $num]) AND $_POST['export_data_filter_active_' . $num] == 'on')		{			$export_data_filter_active = 'on';		}		else {$export_data_filter_active = '';}				$sql_statement = 'INSERT INTO sweeps_export_data_filters SET' 														.'  profile_id = "'.$profile_id.'"'											.', filter_id = "'.$num.'"'											.', active = "'.$export_data_filter_active.'"'											.', filter_name = "'.$_POST['export_data_filter_name_' . $num].'"'											.', filter_operator = "'.$_POST['export_data_filter_operator_' . $num].'"'											.', filter_data = "'.$_POST['export_data_filter_data_' . $num].'"'											;		UpdateDB ($sql_statement, $mysql_err_msg);		$sql_statement = 'INSERT INTO sweeps_export_data_order_by SET' 														.'  profile_id = "'.$profile_id.'"'											.', order_by_id = "'.$num.'"'											.', fields_order_by = "'.$_POST['export_data_order_by_' . $num].'"'											;			UpdateDB ($sql_statement, $mysql_err_msg);				//	===========================	EDITED TO HERE	=======================================================================			}		//	Do Export Data		if (isset($_POST['submit_export_data']))	{		$sent_key =1;		require_once ('cms_export_data.php');			}	else	{		header('location: '.$this_page.'&profile_id='.$profile_id); 		exit();		}	?>